INSERT INTO `news` (`id`, `title`, `content`, `images`, `id_member`, `views`, `created_at`) VALUES
(1, 'BÍ QUYẾT ĐỂ GIỮ TÓC TẨY LÂU PHAI: 5 ĐIỀU TỐI KỴ CẦN TRÁNH', '<p>V&agrave;i năm trở lại đ&acirc;y, người ta kh&ocirc;ng kh&oacute; bắt gặp h&igrave;nh ảnh c&aacute;c bạn trẻ với những m&aacute;i t&oacute;c đủ m&agrave;u sắc từ xanh, đỏ, hồng, t&iacute;m, kh&oacute;i. Đ&acirc;y kh&ocirc;ng chỉ l&agrave; xu thế của giới trẻ m&agrave; c&ograve;n g&oacute;p phần l&agrave;m tăng sự c&aacute; t&iacute;nh, độc đ&aacute;o, mới lạ cho những anh ch&agrave;ng muốn theo đuổi phong c&aacute;ch trẻ trung, hiện đại.&nbsp;</p>\r\n<figure class=\"wp-block-image\" style=\"margin: 10px 0px 1em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; display: block; max-width: 100%; color: #818181; background-color: #ffffff;\"><img class=\"wp-image-22178\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; max-width: 100%; height: auto !important;\" src=\"http://blog.30shine.com/wp-content/uploads/2020/07/61585944_2316906351858487_2222135144586674176_n.jpg\" sizes=\"(max-width: 603px) 100vw, 603px\" srcset=\"https://blog.30shine.com/wp-content/uploads/2020/07/61585944_2316906351858487_2222135144586674176_n.jpg 603w, https://blog.30shine.com/wp-content/uploads/2020/07/61585944_2316906351858487_2222135144586674176_n-201x300.jpg 201w\" alt=\"\" data-pagespeed-url-hash=\"958595616\" /></figure>\r\n<p style=\"margin: 0px 0px 20px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; color: #818181; background-color: #ffffff;\">T&oacute;c sau khi tẩy nhuộm thường gặp nhiều vấn đề về kh&ocirc; xơ do mất đi độ ẩm tự nhi&ecirc;n, tuy nhi&ecirc;n, nếu kh&ocirc;ng sử dụng thuốc nhuộm t&oacute;c chất lượng v&agrave; l&agrave;m t&oacute;c ở những salon kh&ocirc;ng uy t&iacute;n th&igrave; m&aacute;i t&oacute;c của bạn sẽ c&ograve;n gặp nhiều vấn đề hư tổn hơn nữa. Dưới đ&acirc;y 30Shine sẽ m&aacute;ch bạn 5 b&iacute; k&iacute;p để bảo vệ v&agrave; chăm s&oacute;c cho t&oacute;c tẩy.</p>\r\n<figure class=\"wp-block-image\" style=\"margin: 10px 0px 1em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; display: block; max-width: 100%; color: #818181; background-color: #ffffff;\"><img class=\"wp-image-22177\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; max-width: 100%; height: auto !important;\" src=\"http://blog.30shine.com/wp-content/uploads/2020/07/86719842_2531371560411964_7049664426646962176_n.jpg\" sizes=\"(max-width: 900px) 100vw, 900px\" srcset=\"https://blog.30shine.com/wp-content/uploads/2020/07/86719842_2531371560411964_7049664426646962176_n.jpg 900w, https://blog.30shine.com/wp-content/uploads/2020/07/86719842_2531371560411964_7049664426646962176_n-150x150.jpg 150w, https://blog.30shine.com/wp-content/uploads/2020/07/86719842_2531371560411964_7049664426646962176_n-300x300.jpg 300w, https://blog.30shine.com/wp-content/uploads/2020/07/86719842_2531371560411964_7049664426646962176_n-768x768.jpg 768w, https://blog.30shine.com/wp-content/uploads/2020/07/86719842_2531371560411964_7049664426646962176_n-55x55.jpg 55w\" alt=\"\" data-pagespeed-url-hash=\"1627003868\" /></figure>\r\n<h2 style=\"margin: 0px 0px 12px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 24px; line-height: 40px; font-family: Roboto, sans-serif; vertical-align: baseline; color: #444444; background-color: #ffffff;\">1. Thường xuy&ecirc;n phơi t&oacute;c dưới &aacute;nh nắng mặt trời trong thời gian d&agrave;i</h2>\r\n<p style=\"margin: 0px 0px 20px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; color: #818181; background-color: #ffffff;\">Khi tẩy, thuốc sẽ ngấm v&agrave;o t&oacute;c l&agrave;m sợi t&oacute;c nở ra, lớp biểu b&igrave; bọc sợi t&oacute;c bị vỡ. Lượng melanin trong t&oacute;c tẩy sẽ bị giảm đ&aacute;ng kể so với t&oacute;c thường khiến t&oacute;c mất đi khả năng tự bảo vệ. Dưới thời tiết 40 độ của m&ugrave;a h&egrave;, kh&ocirc;ng chỉ l&agrave;n da m&agrave; m&aacute;i t&oacute;c cũng dễ d&agrave;ng bị những tia UV xấu xa x&acirc;m hại. Bởi vậy, khi ra ngo&agrave;i đặc biệt l&agrave; v&agrave;o l&uacute;c trời nắng gắt, h&atilde;y đội mũ để che k&iacute;n t&oacute;c tr&aacute;nh tiếp x&uacute;c trực tiếp dưới &aacute;nh nắng mặt trời.</p>\r\n<h2 style=\"margin: 0px 0px 12px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 24px; line-height: 40px; font-family: Roboto, sans-serif; vertical-align: baseline; color: #444444; background-color: #ffffff;\">2. Gội đầu ngay lập tức trong 3 ng&agrave;y đầu ti&ecirc;n</h2>\r\n<p style=\"margin: 0px 0px 20px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; color: #818181; background-color: #ffffff;\">Đ&acirc;y l&agrave; điều đầu ti&ecirc;n nhưng cực k&igrave; quan trọng m&agrave; bất kể stylist n&agrave;o cũng sẽ dặn anh ngay sau khi tẩy t&oacute;c. 2-3 ng&agrave;y l&agrave; khoảng thời gian để t&oacute;c ngấm thuốc nhuộm n&ecirc;n anh em tr&aacute;nh gội đầu ngay nếu kh&ocirc;ng muốn t&oacute;c phai nhanh.</p>\r\n<figure class=\"wp-block-image\" style=\"margin: 10px 0px 1em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; display: block; max-width: 100%; color: #818181; background-color: #ffffff;\"><img class=\"wp-image-22179\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; max-width: 100%; height: auto !important;\" src=\"https://blog.30shine.com/wp-content/uploads/2020/07/Sau-nhuom-toc-can-cham-soc-nhu-the-nao-30Shine.jpg\" sizes=\"(max-width: 800px) 100vw, 800px\" srcset=\"https://blog.30shine.com/wp-content/uploads/2020/07/Sau-nhuom-toc-can-cham-soc-nhu-the-nao-30Shine.jpg 800w, https://blog.30shine.com/wp-content/uploads/2020/07/Sau-nhuom-toc-can-cham-soc-nhu-the-nao-30Shine-300x199.jpg 300w, https://blog.30shine.com/wp-content/uploads/2020/07/Sau-nhuom-toc-can-cham-soc-nhu-the-nao-30Shine-768x510.jpg 768w\" alt=\"\" data-pagespeed-url-hash=\"3626817289\" /></figure>\r\n<h2 style=\"margin: 0px 0px 12px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 24px; line-height: 40px; font-family: Roboto, sans-serif; vertical-align: baseline; color: #444444; background-color: #ffffff;\">3. Thường xuy&ecirc;n tiếp x&uacute;c với hơi n&oacute;ng, nước n&oacute;ng</h2>\r\n<p style=\"margin: 0px 0px 20px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; color: #818181; background-color: #ffffff;\">Kh&ocirc;ng chỉ t&oacute;c tẩy m&agrave; ngay cả t&oacute;c nhuộm cũng n&ecirc;n hạn chế sử dụng c&aacute;c sản phẩm như m&aacute;y uốn, sấy, c&aacute;c d&ograve;ng m&aacute;y tạo kiểu t&oacute;c để t&oacute;c kh&ocirc;ng bị hư hại th&ecirc;m. Nhiệt độ cao từ những chiếc m&aacute;y n&agrave;y sẽ c&ograve;n c&oacute; thể l&agrave;m cho t&oacute;c anh bị bay m&agrave;u rất nhanh, đặc biệt l&agrave; những t&ocirc;ng m&agrave;u nhuộm s&aacute;ng như kh&oacute;i.</p>\r\n<figure class=\"wp-block-image\" style=\"margin: 10px 0px 1em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; display: block; max-width: 100%; color: #818181; background-color: #ffffff;\"><img class=\"wp-image-22180\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; max-width: 100%; height: auto !important;\" src=\"https://blog.30shine.com/wp-content/uploads/2020/07/mens-hair-dryer.jpg\" sizes=\"(max-width: 860px) 100vw, 860px\" srcset=\"https://blog.30shine.com/wp-content/uploads/2020/07/mens-hair-dryer.jpg 860w, https://blog.30shine.com/wp-content/uploads/2020/07/mens-hair-dryer-300x200.jpg 300w, https://blog.30shine.com/wp-content/uploads/2020/07/mens-hair-dryer-768x512.jpg 768w\" alt=\"\" data-pagespeed-url-hash=\"1071023391\" /></figure>\r\n<p style=\"margin: 0px 0px 20px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; color: #818181; background-color: #ffffff;\">Nếu muốn tạo kiểu bằng m&aacute;y, h&atilde;y nhớ b&ocirc;i hoặc xịt dưỡng t&oacute;c để tạo lớp m&agrave;ng bảo vệ cho t&oacute;c.&nbsp;Đặc biệt để chăm s&oacute;c t&oacute;c tốt nhất, cho d&ugrave; l&agrave; m&ugrave;a đ&ocirc;ng anh cũng chỉ n&ecirc;n d&ugrave;ng nước vừa đủ ấm, tr&aacute;nh d&ugrave;ng nước n&oacute;ng. Nước qu&aacute; n&oacute;ng rất c&oacute; hại cho t&oacute;c tẩy v&agrave; da đầu.</p>\r\n<h2 style=\"margin: 0px 0px 12px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 24px; line-height: 40px; font-family: Roboto, sans-serif; vertical-align: baseline; color: #444444; background-color: #ffffff;\">4. Chỉ nhuộm l&agrave; xong chứ kh&ocirc;ng chăm s&oacute;c</h2>\r\n<figure class=\"wp-block-image\" style=\"margin: 10px 0px 1em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; display: block; max-width: 100%; color: #818181; background-color: #ffffff;\"><img class=\"wp-image-22182\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; max-width: 100%; height: auto !important;\" src=\"https://blog.30shine.com/wp-content/uploads/2020/07/cham-soc-toc-hay-cho-nam-gioi.jpg\" sizes=\"(max-width: 550px) 100vw, 550px\" srcset=\"https://blog.30shine.com/wp-content/uploads/2020/07/cham-soc-toc-hay-cho-nam-gioi.jpg 550w, https://blog.30shine.com/wp-content/uploads/2020/07/cham-soc-toc-hay-cho-nam-gioi-300x242.jpg 300w\" alt=\"\" data-pagespeed-url-hash=\"1366532948\" /></figure>\r\n<p style=\"margin: 0px 0px 20px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; color: #818181; background-color: #ffffff;\">Nếu muốn đẹp th&igrave; kh&ocirc;ng thể lười. T&oacute;c đ&atilde; qua tẩy rồi thường yếu hơn v&agrave; dễ kh&ocirc; xơ. Nếu kh&ocirc;ng thể thường xuy&ecirc;n qua salon hấp t&oacute;c th&igrave; h&atilde;y sắm những sản phẩm dưỡng chuy&ecirc;n dụng d&agrave;nh ri&ecirc;ng cho t&oacute;c tẩy như mặt nạ t&oacute;c, dưỡng c&oacute; chứa protein để phục hồi độ chắc khỏe cho t&oacute;c.</p>\r\n<h2 style=\"margin: 0px 0px 12px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 24px; line-height: 40px; font-family: Roboto, sans-serif; vertical-align: baseline; color: #444444; background-color: #ffffff;\">5. Sử dụng dầu gội thường tinh chất bạc h&agrave;</h2>\r\n<p style=\"margin: 0px 0px 20px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; color: #818181; background-color: #ffffff;\">Đ&atilde; sở hữu một m&aacute;i t&oacute;c c&aacute; t&iacute;nh kh&aacute;c biệt th&igrave; cũng đồng nghĩa với việc anh kh&ocirc;ng n&ecirc;n d&ugrave;ng những loại dầu gội th&ocirc;ng thường, đặc biệt kh&ocirc;ng sử dụng sản phẩm c&oacute; tinh chất bạc h&agrave; &ndash; khắc tinh của t&oacute;c tẩy. Nếu kh&ocirc;ng muốn t&oacute;c bị phai m&agrave;u v&agrave;ng xấu x&iacute; th&igrave; anh h&atilde;y sắm một chai dầu gội t&iacute;m lu&ocirc;n v&agrave; ngay đi v&igrave; dầu t&iacute;m gi&uacute;p trung h&ograve;a sắc v&agrave;ng hay đỏ xỉn tr&ecirc;n t&oacute;c tẩy, giữ cho m&agrave;u t&oacute;c chuẩn, bền m&agrave;u v&agrave; b&oacute;ng hơn.&nbsp;</p>\r\n<figure class=\"wp-block-image\" style=\"margin: 10px 0px 1em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; display: block; max-width: 100%; color: #818181; background-color: #ffffff;\"><img class=\"wp-image-22181\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; max-width: 100%; height: auto !important;\" src=\"https://blog.30shine.com/wp-content/uploads/2020/07/5aedce4a79392e7086026297e59e969c.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://blog.30shine.com/wp-content/uploads/2020/07/5aedce4a79392e7086026297e59e969c.jpg 1024w, https://blog.30shine.com/wp-content/uploads/2020/07/5aedce4a79392e7086026297e59e969c-150x150.jpg 150w, https://blog.30shine.com/wp-content/uploads/2020/07/5aedce4a79392e7086026297e59e969c-300x300.jpg 300w, https://blog.30shine.com/wp-content/uploads/2020/07/5aedce4a79392e7086026297e59e969c-768x768.jpg 768w, https://blog.30shine.com/wp-content/uploads/2020/07/5aedce4a79392e7086026297e59e969c-55x55.jpg 55w\" alt=\"\" data-pagespeed-url-hash=\"1183776771\" /></figure>\r\n<p style=\"margin: 0px 0px 20px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; color: #818181; background-color: #ffffff;\">Ngo&agrave;i những b&iacute; k&iacute;p tr&ecirc;n, anh em n&ecirc;n hiểu rằng khi đ&atilde; quyết định tẩy/nhuộm t&oacute;c th&igrave; việc t&oacute;c yếu đi l&agrave; điều kh&ocirc;ng thể tr&aacute;nh khỏi. V&igrave; vậy h&atilde;y đưa ra những sự lựa chọn th&ocirc;ng minh như chọn một salon uy t&iacute;n c&oacute; sử dụng thuốc nhuộm ch&iacute;nh h&atilde;ng được nhập khẩu r&otilde; r&agrave;ng sẽ gi&uacute;p anh hạn chế những rủi ro kh&ocirc;ng đ&aacute;ng c&oacute;.</p>\r\n<p style=\"margin: 0px 0px 20px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Roboto, sans-serif; vertical-align: baseline; color: #818181; background-color: #ffffff;\">Đồng thời nếu kh&ocirc;ng thể tự chăm s&oacute;c t&oacute;c tẩy ở nh&agrave;, anh em c&oacute; thể đến 30Shine để phục hồi t&oacute;c hư tổn với Protein chỉ với 99k để cung cấp c&aacute;c dưỡng chất cho t&oacute;c mềm mượt, dễ d&agrave;ng vuốt s&aacute;p tạo kiểu hơn.</p>', '5f24c95fc3d20p1.png', 2, 0, '2020-07-25 13:16:04'),
(2, 'Xu hướng màu tóc nhuộm đẹp 2020 cho nam giới chuẩn hot trend', '<p><span style=\"color: #333333; font-family: Roboto, Helvetica, Arial, sans-serif; font-size: 16px; background-color: #ffffff;\">Mỗi loại m&agrave;u nhuộm đều c&oacute; vai tr&ograve; v&agrave; mang những vẻ đẹp kh&aacute;c nhau tới cho c&aacute;c ch&agrave;ng trai. Dưới đ&acirc;y l&agrave; c&aacute;c m&agrave;u t&oacute;c hot trend nhất hiện nay cho nam theo xu hướng năm 2020&nbsp;</span><a style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #333333; text-decoration-line: none; background-color: #ffffff;\" title=\"menback\" href=\"https://menback.com/\" data-seo-automated-link-building=\"9\">Menback</a><span style=\"color: #333333; font-family: Roboto, Helvetica, Arial, sans-serif; font-size: 16px; background-color: #ffffff;\">&nbsp;giới thiệu tới c&aacute;c bạn.</span></p>\r\n<h2 style=\"box-sizing: border-box; margin: 0.83em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 500; font-stretch: inherit; font-size: 20px; line-height: 1.35; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #212121; text-rendering: optimizelegibility; background-color: #ffffff;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: bold; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">1. T&oacute;c nam nhuộm m&agrave;u v&agrave;ng s&aacute;ng</span></h2>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">M&agrave;u v&agrave;ng s&aacute;ng kh&ocirc;ng chỉ l&agrave; t&ocirc;ng m&agrave;u s&aacute;ng được nhiều giới trẻ ưu chuộng nhất m&agrave; c&ograve;n ph&ugrave; hợp c&aacute;c m&ugrave;a trong năm bởi n&oacute; tạo cảm gi&aacute;c l&agrave;n da bạn ấm &aacute;p v&agrave; hồng h&agrave;o hơn v&agrave;o đ&ocirc;ng v&agrave; đem tới cảm gi&aacute;c m&aacute;t mẻ v&agrave;o m&ugrave;a h&egrave;.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">Những kiểu t&oacute;c xoăn nam theo phong c&aacute;ch H&agrave;n Quốc c&agrave;ng l&ocirc;i cuốn hơn, c&aacute; t&iacute;nh hơn khi kết hợp c&aacute;c t&ocirc;ng m&agrave;u nổi bật nhất mang phong c&aacute;ch đẹp v&agrave; sang trọng nhất. Với nhiều m&agrave;u sắc đẹp nhất định sẽ mang lại cho nam giới một c&aacute; t&iacute;nh của ri&ecirc;ng m&igrave;nh.</p>\r\n<figure id=\"attachment_6789\" class=\"wp-caption aligncenter\" style=\"box-sizing: border-box; margin-top: 5px; margin-bottom: 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; display: block; text-align: center; max-width: 100%; color: #333333; background-color: #ffffff; width: 488px;\" aria-describedby=\"caption-attachment-6789\"><img class=\"wp-image-6789 size-large\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px none; font: inherit; vertical-align: middle; max-width: 100%; height: auto; width: auto;\" src=\"https://menback.com/wp-content/uploads/2020/02/toc-nam-nhuom-mau-vang-dep-488x506.jpg\" sizes=\"(max-width: 488px) 100vw, 488px\" srcset=\"https://menback.com/wp-content/uploads/2020/02/toc-nam-nhuom-mau-vang-dep-488x506.jpg 488w, https://menback.com/wp-content/uploads/2020/02/toc-nam-nhuom-mau-vang-dep-386x400.jpg 386w, https://menback.com/wp-content/uploads/2020/02/toc-nam-nhuom-mau-vang-dep-768x796.jpg 768w, https://menback.com/wp-content/uploads/2020/02/toc-nam-nhuom-mau-vang-dep-750x777.jpg 750w, https://menback.com/wp-content/uploads/2020/02/toc-nam-nhuom-mau-vang-dep.jpg 800w\" alt=\"toc nam nhuom mau vang dep\" width=\"488\" height=\"506\" data-pin-no-hover=\"true\" />\r\n<figcaption id=\"caption-attachment-6789\" class=\"wp-caption-text\" style=\"box-sizing: border-box; margin: 3px 0px 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 12px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: #a0a0a0;\">Xu hướng m&agrave;u t&oacute;c nhuộm đẹp 2020 cho nam giới chuẩn hot trend.</figcaption>\r\n</figure>\r\n<h2 style=\"box-sizing: border-box; margin: 0.83em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 500; font-stretch: inherit; font-size: 20px; line-height: 1.35; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #212121; text-rendering: optimizelegibility; background-color: #ffffff;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: bold; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">2. Xu hướng m&agrave;u t&oacute;c n&acirc;u chocolate trở lại</span></h2>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">Một m&agrave;u t&oacute;c kh&ocirc;ng thể thiếu trong BST c&aacute;c m&agrave;u t&oacute;c 2020 sắp tới. C&aacute;c bạn sẽ kh&ocirc;ng phải đầu tư qu&aacute; nhiều thời gian trong việc lựa chọn trang phục ph&ugrave; hợp với m&agrave;u t&oacute;c n&agrave;y. Nhiều chuy&ecirc;n gia t&oacute;c c&ograve;n dự đo&aacute;n: M&agrave;u n&acirc;u chocolate n&agrave;y c&oacute; thể sẽ tạo n&ecirc;n xu hướng m&agrave;u t&oacute;c đẹp 2020 sắp tới. Chocolate l&agrave; m&agrave;u kh&ocirc;ng mới nhưng chưa bao giờ hết hot, nếu bạn muốn c&oacute; một m&aacute;i t&oacute;c kh&ocirc;ng qu&aacute; nổi bật th&igrave; đ&acirc;y sẽ l&agrave; một gợi &yacute; m&agrave;u t&oacute;c ho&agrave;n hảo cho nam giới. Vẻ tự nhi&ecirc;n của chocolate sẽ gi&uacute;p t&ocirc;n da, khiến gương mặt bạn trở n&ecirc;n tươi s&aacute;ng v&agrave; rạng ngời hơn.</p>\r\n<figure id=\"attachment_6788\" class=\"wp-caption aligncenter\" style=\"box-sizing: border-box; margin-top: 5px; margin-bottom: 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; display: block; text-align: center; max-width: 100%; color: #333333; background-color: #ffffff; width: 640px;\" aria-describedby=\"caption-attachment-6788\"><img class=\"wp-image-6788 size-full\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px none; font: inherit; vertical-align: middle; max-width: 100%; height: auto; width: auto;\" src=\"https://menback.com/wp-content/uploads/2020/02/toc-nam-mau-nau-chocolate.jpg\" sizes=\"(max-width: 640px) 100vw, 640px\" srcset=\"https://menback.com/wp-content/uploads/2020/02/toc-nam-mau-nau-chocolate.jpg 640w, https://menback.com/wp-content/uploads/2020/02/toc-nam-mau-nau-chocolate-400x309.jpg 400w\" alt=\"toc nam mau nau chocolate\" width=\"640\" height=\"494\" data-pin-no-hover=\"true\" />\r\n<figcaption id=\"caption-attachment-6788\" class=\"wp-caption-text\" style=\"box-sizing: border-box; margin: 3px 0px 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 12px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: #a0a0a0;\">Xu hướng m&agrave;u t&oacute;c nhuộm đẹp 2020 cho nam giới chuẩn hot trend</figcaption>\r\n</figure>\r\n<h2 style=\"box-sizing: border-box; margin: 0.83em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 500; font-stretch: inherit; font-size: 20px; line-height: 1.35; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #212121; text-rendering: optimizelegibility; background-color: #ffffff;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: bold; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">3. T&oacute;c nam nhuộm m&agrave;u n&acirc;u gỗ 2020</span></h2>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">Vừa bắt kịp m&ugrave;a mốt t&oacute;c 2020 lại gi&uacute;p l&agrave; da s&aacute;ng, tươi trẻ hơn. Nhưng m&agrave;u n&acirc;u gỗ n&agrave;y hơi k&eacute;n t&oacute;c, n&oacute; kh&ocirc;ng th&iacute;ch hợp với nam giới để t&oacute;c ngắn, c&ograve;n nếu để t&oacute;c d&agrave;i th&igrave; đ&acirc;y sẽ l&agrave; lợi thế để mang lại cảm gi&aacute;c ấm &aacute;p cho gương mặt v&agrave; l&agrave;n da.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">Th&ocirc;ng thường, da của con người được chia ra l&agrave;m 2 loại: t&ocirc;ng m&agrave;u da lanh v&agrave; t&ocirc;ng m&agrave;u da n&oacute;ng. Để ph&acirc;n biệt t&ocirc;ng m&agrave;u n&agrave;y c&oacute; một c&aacute;ch dễ d&agrave;ng, khi bạn nh&igrave;n da m&igrave;nh dưới &aacute;nh s&aacute;ng mặt trời sẽ xuất hiện những đường g&acirc;n dưới da. Nếu đường g&acirc;n hiển thị m&agrave;u xanh dương th&igrave; bạn thuộc tu&yacute;p người c&oacute; da t&ocirc;ng lạnh. Ngược lại, nếu g&acirc;n c&oacute; m&agrave;u xanh l&aacute; c&acirc;y th&igrave; bạn c&oacute; da t&ocirc;ng n&oacute;ng. C&ograve;n nếu c&oacute; cả 2 g&acirc;n n&agrave;y gần bằng nhau th&igrave; da bạn thuộc t&ocirc;ng da trung t&iacute;nh.</p>\r\n<figure id=\"attachment_6787\" class=\"wp-caption aligncenter\" style=\"box-sizing: border-box; margin-top: 5px; margin-bottom: 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; display: block; text-align: center; max-width: 100%; color: #333333; background-color: #ffffff; width: 500px;\" aria-describedby=\"caption-attachment-6787\"><img class=\"wp-image-6787 size-full\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px none; font: inherit; vertical-align: middle; max-width: 100%; height: auto; width: auto;\" src=\"https://menback.com/wp-content/uploads/2020/02/toc-nam-mau-nau-go-2020.jpg\" sizes=\"(max-width: 500px) 100vw, 500px\" srcset=\"https://menback.com/wp-content/uploads/2020/02/toc-nam-mau-nau-go-2020.jpg 500w, https://menback.com/wp-content/uploads/2020/02/toc-nam-mau-nau-go-2020-400x400.jpg 400w, https://menback.com/wp-content/uploads/2020/02/toc-nam-mau-nau-go-2020-75x75.jpg 75w\" alt=\"toc nam mau nau go 2020\" width=\"500\" height=\"501\" data-pin-no-hover=\"true\" />\r\n<figcaption id=\"caption-attachment-6787\" class=\"wp-caption-text\" style=\"box-sizing: border-box; margin: 3px 0px 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 12px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: #a0a0a0;\">Xu hướng m&agrave;u t&oacute;c nhuộm đẹp 2020 cho nam giới chuẩn hot trend</figcaption>\r\n</figure>\r\n<h2 style=\"box-sizing: border-box; margin: 0.83em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 500; font-stretch: inherit; font-size: 20px; line-height: 1.35; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #212121; text-rendering: optimizelegibility; background-color: #ffffff;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: bold; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">4. T&oacute;c nam nhuộm m&agrave;u n&acirc;u đỏ</span></h2>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">M&agrave;u n&acirc;u đỏ gi&uacute;p da mặt nam giới tr&ocirc;ng s&aacute;ng hơn nhưng vẫn đảm bảo yếu tổ&nbsp;<a style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: #333333; text-decoration-line: none;\" title=\"thời trang\" href=\"https://menback.com/thoi-trang\" data-seo-automated-link-building=\"2\">thời trang</a>. Tuy nhi&ecirc;n, để c&oacute; được m&aacute;i t&oacute;c đẹp, khi nhuộm t&oacute;c phải c&acirc;n bằng tỷ lệ ho&agrave;n hảo giữa m&agrave;u n&acirc;u v&agrave; đỏ. V&igrave; nếu c&oacute; sai lệch th&igrave; sẽ th&agrave;nh t&ocirc;ng m&agrave;u đỏ. V&agrave; t&ocirc;ng m&agrave;u n&agrave;y sẽ nổi bật hơn rất nhiều những g&igrave; anh em tưởng tượng trước đ&oacute;.</p>\r\n<figure id=\"attachment_6786\" class=\"wp-caption aligncenter\" style=\"box-sizing: border-box; margin-top: 5px; margin-bottom: 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; display: block; text-align: center; max-width: 100%; color: #333333; background-color: #ffffff; width: 500px;\" aria-describedby=\"caption-attachment-6786\"><img class=\"wp-image-6786 size-full\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px none; font: inherit; vertical-align: middle; max-width: 100%; height: auto; width: auto;\" src=\"https://menback.com/wp-content/uploads/2020/02/xu-huong-mau-toc-nam-2020.jpg\" sizes=\"(max-width: 500px) 100vw, 500px\" srcset=\"https://menback.com/wp-content/uploads/2020/02/xu-huong-mau-toc-nam-2020.jpg 500w, https://menback.com/wp-content/uploads/2020/02/xu-huong-mau-toc-nam-2020-400x266.jpg 400w\" alt=\"xu huong mau toc nam 2020\" width=\"500\" height=\"333\" data-pin-no-hover=\"true\" />\r\n<figcaption id=\"caption-attachment-6786\" class=\"wp-caption-text\" style=\"box-sizing: border-box; margin: 3px 0px 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 12px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: #a0a0a0;\">Xu hướng m&agrave;u t&oacute;c nhuộm đẹp 2020 cho nam giới chuẩn hot trend</figcaption>\r\n</figure>\r\n<h2 style=\"box-sizing: border-box; margin: 0.83em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 500; font-stretch: inherit; font-size: 20px; line-height: 1.35; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #212121; text-rendering: optimizelegibility; background-color: #ffffff;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: bold; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">5. M&agrave;u t&oacute;c nam nhuộm highlight</span></h2>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">T&oacute;c highlight l&agrave; một lựa chọn ph&ugrave; hợp với hầu hết mọi kiểu đầu d&agrave;nh cho c&aacute;c ch&agrave;ng. Khi nhuộm highlight c&aacute;c lớp t&oacute;c nhuộm xen kẽ với m&agrave;u t&oacute;c gốc sẽ l&agrave; một điểm nhấn ấn tượng. Với m&aacute;i t&oacute;c đen, bạn c&oacute; thể chọn highlight m&agrave;u n&acirc;u hoặc v&agrave;ng sẫm. Với m&aacute;i t&oacute;c m&agrave;u hơi s&aacute;ng sẽ ph&ugrave; hợp với highlight m&agrave;u v&agrave;ng s&aacute;ng hoặc bạch kim.</p>\r\n<figure id=\"attachment_6785\" class=\"wp-caption aligncenter\" style=\"box-sizing: border-box; margin-top: 5px; margin-bottom: 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; display: block; text-align: center; max-width: 100%; color: #333333; background-color: #ffffff; width: 434px;\" aria-describedby=\"caption-attachment-6785\"><img class=\"wp-image-6785 size-large\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px none; font: inherit; vertical-align: middle; max-width: 100%; height: auto; width: auto;\" src=\"https://menback.com/wp-content/uploads/2020/02/toc-nam-highlight-2020-434x506.jpg\" sizes=\"(max-width: 434px) 100vw, 434px\" srcset=\"https://menback.com/wp-content/uploads/2020/02/toc-nam-highlight-2020-434x506.jpg 434w, https://menback.com/wp-content/uploads/2020/02/toc-nam-highlight-2020-343x400.jpg 343w, https://menback.com/wp-content/uploads/2020/02/toc-nam-highlight-2020-768x895.jpg 768w, https://menback.com/wp-content/uploads/2020/02/toc-nam-highlight-2020-750x874.jpg 750w, https://menback.com/wp-content/uploads/2020/02/toc-nam-highlight-2020.jpg 800w\" alt=\"toc nam highlight 2020\" width=\"434\" height=\"506\" data-pin-no-hover=\"true\" />\r\n<figcaption id=\"caption-attachment-6785\" class=\"wp-caption-text\" style=\"box-sizing: border-box; margin: 3px 0px 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 12px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: #a0a0a0;\">Xu hướng m&agrave;u t&oacute;c nhuộm đẹp 2020 cho nam giới chuẩn hot trend</figcaption>\r\n</figure>\r\n<h2 style=\"box-sizing: border-box; margin: 0.83em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 500; font-stretch: inherit; font-size: 20px; line-height: 1.35; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #212121; text-rendering: optimizelegibility; background-color: #ffffff;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: bold; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">6. Xu hướng nhuộm t&oacute;c m&agrave;u rượu vang</span></h2>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">M&agrave;u đỏ rượu vang rất ph&ugrave; hợp với những ch&agrave;ng trai y&ecirc;u th&iacute;ch sự lịch l&atilde;m v&agrave; phong c&aacute;ch hiện đại trẻ trung. Do đ&oacute;, đ&acirc;y m&agrave;u t&oacute;c m&agrave; nam giới kh&ocirc;ng thể kh&ocirc;ng thử một lần trong đời để l&agrave;m mới bản th&acirc;n. M&agrave;u rượu vang gi&uacute;p da mặt bạn tr&ocirc;ng s&aacute;ng hơn v&agrave; cũng cực kỳ thời trang nữa đ&oacute;.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">Một bộ vest lịch l&atilde;m hay một bộ trang phục bụi bặm c&aacute; t&iacute;nh cũng rất hợp m&aacute;i t&oacute;c nhuộm m&agrave;u đỏ rượu vang n&agrave;y n&ecirc;n bạn c&oacute; thể dễ d&agrave;ng thay đổi phong c&aacute;ch của m&igrave;nh đa dạng hơn. M&agrave;u nhuộm rượu vang mang đến cho nam giới sự trẻ trung năng động. M&agrave;u n&agrave;y cũng l&agrave;m cho da mặt của ch&uacute;ng ta s&aacute;ng hơn tạo cảm gi&aacute;c cuốn h&uacute;t.</p>\r\n<div class=\"google-auto-placed ap_container\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #333333; background-color: #ffffff; width: 750px; height: auto; clear: none; text-align: center;\"><ins class=\"adsbygoogle adsbygoogle-noablate\" style=\"box-sizing: border-box; margin: auto; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; background: transparent; display: block; height: 280px;\" data-ad-format=\"auto\" data-ad-client=\"ca-pub-5999896178436646\" data-adsbygoogle-status=\"done\"><ins id=\"aswift_2_expand\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: none; font: inherit; vertical-align: baseline; background: transparent; display: inline-table; height: 280px; position: relative; visibility: visible; width: 750px;\"><ins id=\"aswift_2_anchor\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: none; font: inherit; vertical-align: baseline; background: transparent; display: block; height: 280px; position: relative; visibility: visible; width: 750px; overflow: visible;\"><iframe id=\"aswift_2\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border-width: 0px; border-style: initial; font: inherit; vertical-align: baseline; left: 0px; position: absolute; top: 0px; width: 750px; height: 280px;\" src=\"https://googleads.g.doubleclick.net/pagead/ads?client=ca-pub-5999896178436646&amp;output=html&amp;h=280&amp;adk=144910138&amp;adf=4143059224&amp;w=750&amp;fwrn=4&amp;fwrnh=100&amp;lmt=1596271277&amp;num_ads=1&amp;rafmt=1&amp;armr=3&amp;sem=mc&amp;pwprc=5957974524&amp;psa=0&amp;guci=2.2.0.0.2.2.0.0&amp;ad_type=text_image&amp;format